userDefinedLinkHintCss:
div > .vimiumHintMarker.internalVimiumHintMarker {
background: -webkit-gradient(
  linear, left top, left bottom,
  color-stop(0%,#353a3d),
  color-stop(100%,#303538)
);
border: 1px solid #383d40;
}

div > .vimiumHintMarker.internalVimiumHintMarker > span {
color: #00bfff;
font-weight: 1;
font-size: 12px;
text-shadow: #ddd -1px 0;
}

div > .vimiumHintMarker.internalVimiumHintMarker > .matchingCharacter {
color: #ff69b4;
}

#vomnibar {
border-top: 1px solid #cff;
border-left: 1px solid #cff;
box-shadow: 
  -5px 0 5px -3px #cff, 
  0 -5px 5px -3px #cff,
  0 0 20px 20px #333
;
background: transparent;
position: relative;
}

#vomnibar::after {
content: 'gm yt am dd';
color: #aaa;
position: absolute;
top: -16px;
right: 15px;
padding-left: 8px;
padding-right: 8px;
display: inline-block;
background: #333;
--b: 2px solid #cff;
border-top: var(--b);
border-left: var(--b);
border-right: var(--b);
z-index: 100;
}

.vomnibarSearchArea {
background: transparent;
}

#vomnibar input {
--b: 2px solid #fcf;
border-right:  var(--b);
border-bottom: var(--b);
background: transparent;
}

#vomnibar input {
caret-color: #aaa;
}

#vomnibar li span.vomnibarTitle {
color: #dff;
}

#vomnibar li span.vomnibarUrl {
color: #46ee;
}

#vomnibar li.vomnibarSelected {
background: inherit;
}

#vomnibar li.vomnibarSelected::before {
content: '>';
color: #eee;
font-size: 150%;
text-shadow: 0 0 5px #eee;
position: absolute;
top: 30%;
left: 5px;
}

#vomnibar li.vomnibarSelected::after {
content: '<';
color: #eee;
font-size: 150%;
text-shadow: 0 0 5px #eee;
position: absolute;
top: 30%;
right: 5px;
}

body > div.vimiumHUD,
body > div.vimiumHUD > div.vimiumHUDSearchArea {
background-color: #fff2;
}

#hud {
color: #89a;
}

div.vimiumHUD .hud-find.vimiumHUDSearchAreaInner {
background-color: #123;
}

div#hud > span#hud-find-input {
color: #def;
}
